import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FiHome, FiFolder, FiTrendingUp, FiActivity } from 'react-icons/fi';

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    { name: 'Dashboard', path: '/dashboard', icon: FiHome, gradient: 'from-blue-500 to-cyan-500' },
    { name: 'Projects', path: '/projects', icon: FiFolder, gradient: 'from-purple-500 to-pink-500' },
  ];

  return (
    <div className="w-64 glass-effect shadow-xl min-h-screen border-r border-white/20">
      <div className="p-6 border-b border-white/10">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-indigo-500 to-purple-600 flex items-center justify-center shadow-lg">
            <FiActivity className="text-white" size={20} />
          </div>
          <div>
            <h2 className="text-lg font-bold text-gray-800">Menu</h2>
            <p className="text-xs text-gray-500">Navigation</p>
          </div>
        </div>
      </div>

      <nav className="mt-6 px-3 space-y-2">
        {menuItems.map((item) => {
          const Icon = item.icon;
          const isActive = location.pathname === item.path;

          return (
            <Link
              key={item.path}
              to={item.path}
              className={`group flex items-center px-4 py-3.5 rounded-xl text-sm font-medium transition-all ${
                isActive
                  ? 'bg-gradient-to-r from-indigo-500 to-purple-600 text-white shadow-lg shadow-indigo-500/50'
                  : 'text-gray-700 hover:bg-white/70 hover:shadow-md'
              }`}
            >
              <div className={`p-2 rounded-lg mr-3 ${isActive ? 'bg-white/20' : 'bg-gradient-to-r ' + item.gradient + ' bg-opacity-10'}`}>
                <Icon className={isActive ? 'text-white' : 'text-gray-600 group-hover:text-indigo-600'} size={18} />
              </div>
              <span className={isActive ? 'text-white font-semibold' : ''}>{item.name}</span>
              {isActive && (
                <div className="ml-auto">
                  <div className="w-2 h-2 rounded-full bg-white animate-pulse"></div>
                </div>
              )}
            </Link>
          );
        })}
      </nav>

      <div className="absolute bottom-6 left-3 right-3">
        <div className="bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl p-4 text-white shadow-lg">
          <div className="flex items-center space-x-2 mb-2">
            <FiTrendingUp size={20} />
            <h3 className="font-semibold">Pro Tip!</h3>
          </div>
          <p className="text-xs text-white/90">Drag & drop tasks to update their status instantly</p>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
