import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FiHome, FiFolder, FiLogOut, FiUser } from 'react-icons/fi';
import Notifications from '../Notifications/Notifications';

const Navbar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50 border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center space-x-8">
            <Link to="/dashboard" className="flex items-center space-x-2 group">
              <div className="w-10 h-10 rounded-xl bg-indigo-600 flex items-center justify-center shadow-md group-hover:shadow-lg transition-all">
                <span className="text-white font-bold text-xl">T</span>
              </div>
              <span className="text-2xl font-bold text-indigo-600 hidden sm:block">ToggleNest</span>
            </Link>

            <div className="hidden md:flex items-center space-x-1">
              <Link
                to="/dashboard"
                className="flex items-center space-x-2 text-gray-700 hover:text-indigo-600 px-4 py-2 rounded-xl hover:bg-indigo-50 text-sm font-medium transition-smooth"
              >
                <FiHome size={18} />
                <span>Dashboard</span>
              </Link>
              <Link
                to="/projects"
                className="flex items-center space-x-2 text-gray-700 hover:text-indigo-600 px-4 py-2 rounded-xl hover:bg-indigo-50 text-sm font-medium transition-smooth"
              >
                <FiFolder size={18} />
                <span>Projects</span>
              </Link>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <Notifications />

            <div className="flex items-center space-x-3 border-l border-gray-200 pl-4">
              <div className="hidden sm:block text-right">
                <p className="font-semibold text-gray-900">{user.name}</p>
                <p className="text-xs font-medium text-indigo-600">
                  {user.role}
                </p>
              </div>
              <div className="w-10 h-10 rounded-full bg-indigo-600 flex items-center justify-center text-white shadow-md">
                <FiUser size={18} />
              </div>
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-smooth shadow-md"
              >
                <FiLogOut size={16} />
                <span className="hidden sm:inline">Logout</span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
